import {types as t} from "./actions";

const initialState = {
    notes: []
};

export default (state = initialState, action) => {
    switch (action.type) {
        case t.CREATE_NOTE: {
            return { notes: [action.note, ...state.notes] };
        }
        case t.CREATE_NOTE: {
            const { project } = action;
            const notes = {...state.byId};
            projects[project.id] = project;

            return { ...state, byId: projects };
            return { notes: [action.note, ...state.notes] };
        }
        default:
            return state;
    }
}
