import { doFetch } from "./do-fetch";

export function* getNotes() {
    try {
        const requestParams = {
            method: 'GET',
            url: '/notes',
        }

        return yield doFetch(requestParams);
    } catch (error) {
        return error;
    }
}

export function* createNote(note) {
    try {

        console.log('api create')
