        const requestParams = {
            method: 'POST',
            url: '/notes', 
            data: note
        }

        return yield doFetch(requestParams);
    } catch (error) {
        return error;
    }
}

export function* editNote(note) {
    try {

        console.log('api edit')
        console.log('/notes/' + node.id)

        const requestParams = {
            method: 'PATCH',
            url: '/notes/' + node.id, 
            data: note
        }

        return yield doFetch(requestParams);
    } catch (error) {
        return error;
    }
}

export function* deleteNote(note) {
    try {

        console.log('api delete')
        console.log('/notes/' + note.id)

        const requestParams = {
            method: 'DELETE',
            url: '/notes/' + note.id, 
        }

        return yield doFetch(requestParams);
    } catch (error) {
        return error;
    }
}
