import {types as t} from "./actions";

const initialState = {
    notes: []
};

export default (state = initialState, action) => {
    switch (action.type) {
        case t.CREATE_NOTE: {
            return { notes: [action.note, ...state.notes] };
        }
        default:
            return state;
    }
}
