import {types as t} from "./actions";

const initialState = {
    notes: []
};

export default (state = initialState, action) => {
    switch (action.type) {
        case t.CREATE_NOTE: {
            const { note } = action;
            const notes = {...state.notes};
            notes[note.id] = note;

            return { ...state, notes: notes };
        }
        case t.EDIT_NOTE: {
            const { note } = action;
            const notes = {...state.notes};
            notes[note.id] = note;
            console.log(notes)
            return { notes: [notes] };
        }
        default:
            return state;
    }
}
