import {doFetch} from "./do-fetch";

export function* getNotes() {
    try {
        const requestParams = {
            method: 'GET',
            url: '/notes',
        }

        return yield doFetch(requestParams);
    } catch (error) {
        return error;
    }
}

export function* updateNotes(notes) {
    try {
        
        console.log('api')
        console.log(notes)

        const requestParams = {
            method: 'PATCH',
            url: '/notes',
            data: notes
        }

        return yield doFetch(requestParams);
    } catch (error) {
        return error;
    }
}
