import React from "react";
import { StyleSheet, Text, View, Image, Button } from 'react-native';

export default class Notes extends React.Component {

    constructor(props) {

        super(props);

        this.handlePress = this.handlePress.bind(this);
    }

    handlePress = () => {
        this.props.navigation.navigate('Редактирование задачи', { handleSaveNode: this.props.handleSaveNode, note: this.props.note })
    }

    render() {
        return (
            <View style={styles.container}>
                <View style={styles.textContainer}>
                    <Text>{this.props.note.name}</Text>
                </View>
                <View style={styles.textContainer}>
                    <Text>{this.props.note.description}</Text>
                </View>
                <Image style={styles.image} source={{ uri: this.props.note.image }} />
                <View style={styles.buttonContainer}>
                    <Button title="Редактировать" onPress={this.handlePress} />
                </View>
            </View>
        );
    }

}


const styles = StyleSheet.create({
    
    container: {
        marginVertical: 30,
    },

    textContainer: {
        height: 30,
        justifyContent: 'center',
    },

    image: {
        width: 400,
        height: 300,
    },

    buttonContainer: {
        alignItems: 'flex-start',
    },
    
    buttonСontainer: {
        flex: 1,
        alignItems: 'center',
    },

    image: {
        height: 300,
        borderRadius: 20,
        marginTop: 10,
        marginBottom: 5,
    },

    button: {
        backgroundColor: '#8A2BE2',
        borderRadius: 20,
        marginVertical: 10,
        paddingVertical: 10,
        paddingHorizontal: 20,
    },

    buttonText: {
        color: 'white',
        fontSize: 18,
        fontWeight: 'bold'
    },

    nameText: {
        paddingHorizontal: 20,
        paddingVertical: 10,
        marginBottom: 15,
        borderRadius: 20,
        backgroundColor: 'black',
        color: 'white',
        fontSize: 20,
        fontWeight: 'bold',
    },

    descriptionText: {
        paddingHorizontal: 20,
        paddingVertical: 10,
        marginBottom: 5,
        borderRadius: 20,
        backgroundColor: 'black',
        color: 'white',
        fontSize: 16,
        minHeight: 200,
        textAlignVertical: 'top'
    },

})
