export const types = {
    CREATE_NOTE: "CREATE_NOTE",
    EDIT_NOTE: "EDIT_NOTE",
    DELETE_NOTE: "DELETE_NOTE",
}

export const createNote = (note) =>
    ({
        type: types.CREATE_NOTE,
        note,
    })
    
export const editNote = (note) =>
({
    type: types.EDIT_NOTE,
    note,
})
