import {types as t} from "./actions";

const initialState = {
    notes: new Map()
};

export default (state = initialState, action) => {
    switch (action.type) {
        case t.CREATE_NOTE: 
        case t.EDIT_NOTE: {
            const { note } = action;
            var newNotes = new Map(state.notes);
            newNotes.set(note.id, note);
            return { ...state, notes: newNotes };
        }
        case t.DELETE_NOTE: {
            const { note } = action;
            var newNotes = new Map(state.notes);
            newNotes.delete(note.id);
            return { ...state, notes: newNotes };
        }
        default:
            return state;
    }
}
