import {doFetch} from "./do-fetch";

export function* getNotes() {
    try {
        const requestParams = {
            method: 'GET',
            url: '/notes',
        }

        return yield doFetch(requestParams);
    } catch (error) {
        return error;
    }
}

export function* updateNote(nodes) {
    try {
        const requestParams = {
            method: 'POST',
            url: '/notes',
            data: nodes
        }

        return yield doFetch(requestParams);
    } catch (error) {
        return error;
    }
}
