import { takeEvery, put, call } from 'redux-saga/effects';
import * as api from '../../lib/api-client';
import {updateNotesFailure, loadNotesFailure, loadNotesSuccess, types as t} from './actions';

function* loadNotesSaga() {
    try {
        const res = yield api.getNotes();
        const notes = res.data;

        yield put(loadNotesSuccess(notes));
    } catch (error) {
        yield put(loadNotesFailure(error.toString()));
    }
}

function* updateNotesSaga(notes) {
    try {
        console.log()
        yield api.updateNotes(notes);
    } catch (error) {
        yield put(updateNotesFailure(error.toString()));
    }
}

function* notesSaga() {
    yield takeEvery(t.LOAD_NOTES_REQUEST, loadNotesSaga);
    yield takeEvery(t.UPDATE_NOTES_REQUEST, updateNotesSaga);
}

export default notesSaga;
